	select year(vdate) into vyy;
	
	if position('MONROM' in vformatno) then
		select monthrm into vmr 
		from romawi
		where monthcal = vmm
		limit 1;
	end if; 
	
	if (position('YYYY' in vrepeatby) > 0) then
		set lyy = 4;
	else
		if (position('YY' in vrepeatby) > 0) then
			set lyy = 2;
		end if;
	end if;

	if (vrepeatby = '') then
		select ifnull(count(1),0)
		into vcurrvalue
		from snrodet
		where snroid = vsnroid and plantid = vplantid
		limit 1;
		set vtrap = 4;

		if vcurrvalue > 0 then
			select curvalue
			into vcurrvalue
			from snrodet
			where snroid = vsnroid and plantid = vplantid
			limit 1;

			set vcurrvalue=vcurrvalue + 1;

			update snrodet
			set curvalue = vcurrvalue
			where snroid = vsnroid and plantid = vplantid;
		else
			set vcurrvalue=1;
			insert into snrodet (snroid,curdd,curmm,curyy,curvalue,plantid)
			values (vsnroid,0,0,0,1,vplantid);
		end if;
  	else
		if (position('DD' in vrepeatby) > 0) and
		   (position('MM' in vrepeatby) > 0) and
		   (position('YY' in vrepeatby) > 0) then
			select ifnull(count(1),0)
			into vcurrvalue
			from snrodet
			where snroid = vsnroid and curdd = vdd and 
				curmm = vmm and curyy = vyy and plantid = vplantid
			limit 1;
			set vtrap = 3;

			if vcurrvalue > 0 then
				select curvalue
				into vcurrvalue
				from snrodet
				where snroid = vsnroid and curdd = vdd 
					and curmm = vmm and curyy = vyy and plantid = vplantid
				limit 1;
	
				set vcurrvalue=vcurrvalue + 1;
	
				update snrodet
				set curvalue = vcurrvalue
				where snroid = vsnroid and curdd = vdd and curmm = vmm and curyy = vyy 
				and plantid = vplantid;
			else
				set vcurrvalue=1;
				insert into snrodet (snroid,curdd,curmm,curyy,curvalue,plantid)
				values (vsnroid,vdd,vmm,vyy,1,vplantid);
			end if;
  		else
			if (position('MM' in vrepeatby) > 0) and
	   		(position('YY' in vrepeatby) > 0) then
				set vtrap = 2;
				select ifnull(count(1),0)
				into vcurrvalue
				from snrodet
				where snroid = vsnroid and curmm = vmm 
					and curyy = vyy and plantid = vplantid
				limit 1;

				if vcurrvalue > 0 then
					select curvalue
					into vcurrvalue
					from snrodet
					where snroid = vsnroid and curmm = vmm 
						and curyy = vyy and plantid = vplantid
					limit 1;
		
					set vcurrvalue=vcurrvalue + 1;
		
