		else
			if lyy = 2 then
				select replace(vformatdoc,'YY',right(vyy,lyy)) into vformatdoc;
			end if;	
		end if;
	else
		select replace(vformatdoc,'YY',right(vyy,2)) into vformatdoc;
	end if;

